mapper.select:
SELECT a.article_id, count(ac.article_id) commentCounts, a.article_title, a.submit_time, a.article_html_path, a.article_md_path, a.release_time, a.collection_count, a.like_count, a.read_permission, a.state_flag, a.del_flag, a.type_id, u.user_id userId, u.user_name, u.avatar_path, u.sex, u.school, u.major, u.signature, u.email, u.tel, u.role, u.state_flag userStateFlag FROM article_info a LEFT JOIN user_info u ON a.user_id = u.user_id LEFT JOIN (select article_id from article_comment where del_flag = 0) ac on a.article_id = ac.article_id
u.user_id = #{userId}
and a.read_permission = #{readPermission}
and a.type_id & #{typeId} !=0
and a.release_time>#{preReleaseTime}
and a.release_time<#{sufReleaseTime}
and u.user_name = like '%${userName}%'
and a.article_title like '%${articleTitle}%'
GROUP BY a.article_id order by a.release_time desc

SELECT a.article_id, ac.user_id collectorUserId, a.article_title, a.submit_time, a.article_html_path, a.article_md_path, a.release_time, a.collection_count, a.like_count, a.read_permission, a.state_flag, a.del_flag, a.type_id, a.user_id authorUserId, u.user_name, u.avatar_path, u.sex, u.school, u.major, u.signature, u.email, u.tel, u.role, u.state_flag userStateFlag FROM article_info a LEFT JOIN article_collector ac ON ac.article_id = a.article_id LEFT JOIN user_info u ON u.user_id = a.user_id
ac.user_id = #{collectorUserId}
and a.read_permission = #{readPermission}
and a.type_id & #{typeId} !=0
and a.release_time>#{preReleaseTime}
and a.release_time<#{sufReleaseTime}
and u.user_name = like '%${userName}%'
and a.article_title like '%${articleTitle}%'
